\n              document.addEventListener(\"DOMContentLoaded\", function() {\n                var player = document.getElementById(\"player-66398aee5a015\");\n                podlovePlayerCache.add([{\"url\":\"https://www.wppodcast.fr/wp-json/podlove-web-player/shortcode/publisher/1539\",\"data\":{\"version\":5,\"show\":{\"title\":\"Podcast WordPress (franu00e7ais)\",\"subtitle\":\"Informations sur la Communautu00e9 WordPress\",\"summary\":\"Informations, actualitu00e9s et interviews sur la communautu00e9 WordPress.\",\"poster\":\"https://www.wppodcast.fr/podlove/image/68747470733a2f2f7777772e7770706f64636173742e6e65742f77702d636f6e74656e742f75706c6f6164732f73697465732f352f323032302f30382f7770706f64636173742d7371756172652e706e67/500/0/0/podcast-wordpress-francais\",\"link\":\"https://www.wppodcast.fr\"},\"title\":\"Au revoir, u00e9quipe Marketing\",\"subtitle\":\"Avec la cru00e9ation de l\'u00e9quipe Media Corps, nous avons fait nos adieux u00e0 l\'u00e9quipe Marketing apru00e8s 9 ans d\'activitu00e9.\",\"summary\":\"\",\"publicationDate\":\"2024-05-07T00:00:00+00:00\",\"duration\":\"00:11:12.779\",\"poster\":\"https://www.wppodcast.fr/podlove/image/68747470733a2f2f7777772e7770706f64636173742e66722f77702d636f6e74656e742f75706c6f6164732f73697465732f352f323032312f30372f7770706f6463617374626c75652e706e67/500/0/0/au-revoir-equipe-marketing\",\"link\":\"https://www.wppodcast.fr/podcast/au-revoir-equipe-marketing/\",\"chapters\":[],\"audio\":[{\"url\":\"https://www.wppodcast.fr/podlove/file/255/s/webplayer/c/website/wordpress-podcast-fr-e025.mp3\",\"size\":\"15902160\",\"title\":\"MP3 Audio (mp3)\",\"mimeType\":\"audio/mpeg\"}],\"files\":[{\"url\":\"https://www.wppodcast.fr/podlove/file/255/s/webplayer/wordpress-podcast-fr-e025.mp3\",\"size\":\"15902160\",\"title\":\"WordPress Podcast\",\"mimeType\":\"audio/mpeg\"}],\"contributors\":[{\"id\":\"3\",\"name\":\"Patricia\",\"avatar\":\"https://www.wppodcast.fr/wp-content/cache/podlove/2e/61d62ac21b561c55882434e7520f06/patrici